                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Dialogs */}
      <NewOrderDialog
        open={newOrderOpen}
        onOpenChange={setNewOrderOpen}
        lieferanten={lieferanten}
        onSuccess={fetchData}
      />

      {selectedOrder && (
        <OrderDetailDialog
          order={selectedOrder}
          lieferantMap={lieferantMap}
          positionen={positionen}
          artikelMap={artikelMap}
          open={!!selectedOrder}
          onOpenChange={(open) => {
