      {selectedSupplier && (
        <SupplierDetailDialog
          supplier={selectedSupplier}
          orders={bestellungen}
          open={!!selectedSupplier}
          onOpenChange={(open) => {
            if (!open) setSelectedSupplier(null);
          }}
        />
      )}
    </div>
  );
}

